import React, {useState} from 'react';



function Registro() {
/** variables de estado */
const [name, setName]= useState('');
const [email, setEmail] = useState('');
const [password, serPassword] = useState('');
const [confirmPassword, setConfirmPassword] = useState('');
const [capture, setCapture] = useState(false);

function handleChange (e){
    const {name, value} = e.target;
    switch (name) {
        case 'name':
                    setName(value);
            break;
        case 'emaail':
                    setEmail(value);
            break;
        case 'password':
                    serPassword(value);
            break;        
        default:
                    setConfirmPassword(value);
            break;
    }

}
    return (
        <div className="Registro">
            <div className="container">
                <div className="row">
                    <div className="col-md-4 mx-auto">
                        <div className="card mt-4 text.center">
                            <div className="card-herder">
                                <h1>register account</h1>
                            </div>
                            <div className="card-body">
                                <form>
                                    <div className="form-group">
                                        <input name="name" placeholder="nombre" value = {name} onChange = {handleChange}/>
                                    </div>
                                    <div className="form-group">
                                        <input type="email" name="email" value = {email} onChange = {handleChange} placeholder=" email" />
                                    </div>
                                    <div className="form-group">
                                        <input type="password" name="password" value = {password} onChange = {handleChange} placeholder=" password" />
                                    </div>

                                    <div className="form-group">
                                        <input type="password" name="confirmPassword" value = {confirmPassword} onChange = {handleChange} placeholder=" confirPassword" />
                                    </div>
                                    <button type="submit" className="btn btn-primary">register</button>
                                </form>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>

    );
}
export default Registro;
